import React from "react";

function App() {
  return (
    <>
      {/* Red Section (previously from HTML) */}
      <div className="container-fluid p-5 bg-danger text-white text-center">
        <h1>This comes from the index page</h1>
        <p>Resize this responsive page to see the effect!</p>
      </div>

      {/* Blue Section (from App.js) */}
      <div className="container-fluid p-5 bg-primary text-white text-center">
        <h1>This comes from the component (App)</h1>
        <p>Resize this responsive page to see the effect!</p>
      </div>

      {/* Grey Section (from index.js) */}
      <div className="container mt-3 bg-dark-subtle">
        <h2>This comes from index.js (now moved here)</h2>
        <p>The .rounded class adds rounded corners to an image:</p>
        <img
          src="cinqueterre.jpg"
          className="rounded"
          alt="Cinque Terre"
          width="304"
          height="236"
        />
      </div>
    </>
  );
}

export default App;
