import React, { useEffect, useState } from 'react';

function Side_work() {
  const [userdata, setUserdata] = useState(0);

  // useEffect runs whenever 'userdata' changes
  useEffect(() => {
    console.log("State is changed:", userdata);
  }, [userdata]); // dependency array with 'userdata'

  // Function to update the state
  const update = () => {
    setUserdata(userdata + 1);
  };

  return (
    <div className='d-flex justify-content-center align-items-center flex-row mt-5'>
      <button onClick={update} className='btn btn-danger'>
        Update
      </button> 
      <h1 className='ms-3'>{userdata}</h1>
    </div>
  );
}

export default Side_work;
