import axios from 'axios';

function Side_work() {
    <a href="const "></a>
  const [userdata, setUserdata] = useState(0);

  
  useEffect(() => {
    console.log("State is changed:", userdata);
  }, [userdata]); 

  
  const update = () => {
    setUserdata(userdata + 1);
  };

  return (
    <div className='d-flex justify-content-center align-items-center  mt-5'>
      <button onClick={update} className='btn btn-danger'>
        Update
      </button> <br/>
      <h1 className='ms-3'>{userdata}</h1>
    </div>
  );
}

export default Side_work;
