import React, { useState } from 'react';
import axios from 'axios';

function Side_work() {
  const [images, setImages] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://picsum.photos/v2/list?page=2&limit=20');
      setImages(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div className='container mt-5'>
      <div className='text-center mb-4'>
        <button onClick={fetchData} className='btn btn-danger'>
          Fetch Images
        </button>
      </div>

      <div className='row'>
        {images.map((img) => (
          <div key={img.id} className='col-12 col-sm-6 col-md-4 col-lg-3 mb-4 text-center'>
            <img
              src={img.download_url}
              alt={img.author}
              className='img-fluid'
              style={{
                width: '100%',
                height: '200px',
                objectFit: 'cover',
                borderRadius: '5px',
              }}
            />
            <p className='mt-2' style={{ fontSize: '0.9rem' }}>{img.author}</p>
          </div>
        ))}
      </div>
     <div className='d-flex justify-content-center mb-4'>
        <button  className='btn ntn-da'>Next</button>
        <button  className='btn ntn-da'>prev</button>
     </div>
    </div>
  );
}

export default Side_work;
