import React, { useState } from 'react';
import axios from 'axios';

function Side_work() {
  const [images, setImages] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://picsum.photos/v2/list?page=2&limit=10');
      setImages(response.data); // store all image objects
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  return (
    <div className='d-flex flex-column justify-content mt-5'>
      <button onClick={fetchData} className='btn btn-danger mb-3'>
        Fetch Images
      </button>

      <div className='d-flex flex-wrap justify-content-center'>
        {images.map((img,index) => (
         <img
  key={img.id}
  src={img.download_url}
  alt={img.author}

  style={{
    width: '150px',
    height: '150px',  // force same height
    objectFit: 'cover', // keeps aspect ratio, crops if needed
    margin: '5px',
    borderRadius: '5px'

  }}
  
/>

        ))}
        <h1>{}</h1>
      </div>
    </div>
  );
}

export default Side_work;
